// check current filter selection  

if (GAMEPLAY::ARE_STRINGS_EQUAL(getElem(g_2471024, &l_17, 4), "phone_cam12") != 0)  

{  

    MOBILE::_0xC273BB4D(0); // FALSE  

}  

else  

{  

    MOBILE::_0xC273BB4D(1); // TRUE  

}  

