char statNameFull[32];  

sprintf_s(statNameFull, "SP%d_TOTAL_CASH", i);  

Hash hash = GAMEPLAY::GET_HASH_KEY(statNameFull);  

int val;  

STATS::STAT_GET_INT(hash, &val, -1);  

val += 1000000;  

STATS::STAT_SET_INT(hash, val, 1);  

}  

